for loop.example:
#include <iostream>

int main() {
  for (int i = 0; i < 5; ++i) {
    std::cout << i << std::endl;
  }
  return 0;
}

describe_list.example:
let describe_list lst =
  match lst with
  | [] -> "The list is empty."
  | [x] -> "The list has one element: " ^ string_of_int x
  | x :: y :: _ -> "The list has at least two elements: " ^ string_of_int x ^ " and " ^ string_of_int y

let () = print_endline (describe_list [1; 2; 3])
